mapper.insert:
insert into tb_documento (documentoId, proyectoId, nombre, fechaInicio)
values (#{documentoId}, #{proyectoId}, #{nombre}, #{fechaInicio})

	select LAST_INSERT_ID();

mapper.update:
update tb_documento set proyectoId = #{proyectoId}, 
 				      nombre = #{nombre}, 
 				      fechaInicio = #{fechaInicio}
where documentoId = #{documentoId}
