mapper.insert:
insert into tb_tarea (tareaId, proyectoId, descripcion, fechaInicio, fechaFin, estado)
values (#{tareaId}, #{proyectoId}, #{descripcion}, #{fechaInicio}, #{fechaFin}, #{estado})

	select LAST_INSERT_ID();

mapper.update:
update tb_tarea set proyectoId = #{proyectoId}, 
 				      descripcion = #{descripcion}, 
 				      fechaInicio = #{fechaInicio}, 
 				      fechaFin = #{fechaFin}, 
 				      estado = #{estado}
where tareaId = #{tareaId}

mapper.select:
select tareaId,proyectoId,descripcion,fechaInicio, fechaFin,estado
from tb_tarea
where tareaId = #{tareaId}
